INSERT INTO product (id, name, category_id, available, quantity, price, description) VALUES
	(1, 'Toshiba Inverter 180 lít GR-B22VU UKG', 1, 0, 20, 5690000, 'Màu sắc sang trọng, thiết kế nhỏ gọn\r\nVới kiểu tủ ngăn đá trên, chiếc tủ lạnh Toshiba Inverter 180 lít GR-B22VU UKG được thiết kế nhỏ gọn nhưng vẫn toát lên vẻ đẹp hiện đại bởi tông màu đen tuyền sang trọng. Dễ dàng đặt ở bất kì vị trí nào trong nhà, từ phòng khách đến phòng bếp.\r\nSiêu tiết kiệm điện với công nghệ biến tần Inverter\r\nNhờ có sự hỗ trợ của công nghệ biến tần Inverter, tủ lạnh Toshiba GR-B22VU UKG sẽ giúp bạn bớt lo lắng về tiền điện mỗi tháng, vì mang lại khả năng tiết kiệm điện hiệu quả so với tủ lạnh thông thường.\r\nCông nghệ biến tần này sẽ kiểm soát sự hoạt động của máy nén giúp duy trì nhiệt độ bên trong tủ và hạn chế tiếng ồn đến mức tối đa.\r\nBảo quản lạnh thực phẩm toàn diện nhờ hệ thống khí lạnh vòng cung\r\nCông nghệ làm lạnh thực phẩm bằng luồng khí vòng cung giúp hơi lạnh được lưu chuyển đến mọi ngóc ngách trong tủ lạnh cũng như khắc phục được tình trạng thực phẩm bị đông đá (do quá lạnh) hay bị hư hao (do thiếu độ lạnh).'),
	(2, 'Samsung Inverter 236 lít RT22M4032BY/SV', 1, 1, 30, 9390000, 'Thịt cá tươi ngon, ăn trong ngày không cần rã đông với ngăn đông mềm -1 độ C Optimal Fresh Zone'),
	(3, 'LG Inverter 315 lít GN-M312BL', 1, 0, 10, 11990000, 'Tủ lạnh LG Inverter 315 Lít GN-M312BL được thiết kế ngăn đá trên với chất liệu cửa tủ bằng thép không gỉ có độ bền cao. Được trang bị công nghệ Inverter giúp tiết kiệm điện năng và làm lạnh từ cánh cửa Door Cooling, khí lạnh lan tỏa đều tại mọi vị trí trong tủ.'),
	(4, 'Sharp Inverter 525 lít SJ-FX600V-SL', 10, 1, 10, 19990000, 'Công nghệ lành lạnh kép Hybrid Cooling phân bổ đều khí lạnh trong tủ\r\nChế độ Express Freezing làm đông thực phẩm xuống tận -24⁰C\r\nĐối với thực phẩm tươi sống, tủ lạnh Sharp Inverter sẽ bảo quản tốt với khả năng đưa nhiệt độ ngăn đông xuống -24⁰C nhanh chóng.\r\nCông nghệ J-tech Inverter tiết kiệm điện năng, vận hành êm ái\r\nKhi sử dụng, tủ lạnh luôn đảm bảo không gây tiếng ồn, đảm bảo sự thoải mái khi dùng. Đặc biệt, công nghệ J-tech sẽ giúp tiết kiệm điện năng tối ưu.\r\nKiểu tủ lạnh 4 cửa với mặt kim loại sang trọng, thời thượng\r\nTủ lạnh Sharp này sở hữu kim loại sang trọng, thời thượng. Thiết kế 4 cửa kiểu Pháp giúp sản phẩm trở thành điểm nổi bật cho nội thất trong nhà.\r\n'),
	(5, 'LG Inverter 394 lít GN-D392BLA', 1, 0, 10, 19490000, 'Dòng tủ lạnh LG 394 lít GN-D392BLA này với những công nghệ hiện đại nổi bật như Door Cooling+, Hygiene Fresh+... cùng với tiện ích lấy nước ngoài tiện lợi kết hợp với gam màu tối ấn tượng, độc đáo. Do đó, sản phẩm sẽ là một sự lựa chọn đáng quan tâm cho gia đình từ 3 - 4 người khi bạn có nhu cầu mua tủ lạnh mới.\r\n'),
	(6, 'Samsung Inverter 488 lít RF48A4010M9/SV', 1, 1, 15, 26090000, 'Sử dụng 2 dàn lạnh độc lập Twin Cooling Plus giúp lạnh đồng đều\r\nThiết kế Multi Door hiện đại, có tiện ích lấy nước bên ngoài\r\nBảo quản thực phẩm tươi ngon nhờ ngăn rau quả giữ ẩm tốt \r\nVới thiết kế có thể lấy nước bên ngoài mà không cần mở tủ, vô cùng tiện lợi.\r\nSử dụng công nghệ Digital Inverter tiết kiệm điện, hoạt động êm ái\r\nKhử mùi hôi khó chịu nhờ bộ lọc than hoạt tính Deodorizer\r\nDung tích sử dụng 488 lít phù hợp với gia đình từ 4 – 5 thành viên'),
	(7, 'Samsung Inverter 655 lít RS62R5001B4/SV', 1, 1, 10, 25090000, 'Thiết kế Side by Side đẳng cấp cùng gam màu đen thời thượng\r\nDung tích 655 lít phù hợp cho gia đình trên 5 người\r\nCông Nghệ Digital Inverter tiết kiệm điện năng hiệu quả\r\nLàm lạnh nhanh chóng, nhiệt độ đồng đều với công nghệ làm lạnh vòm\r\nBộ lọc khử mùi than hoạt tính trả lại không gian trong lành, không bám mùi hôi khó chịu\r\nChuông báo cửa mở phát tín hiệu cảnh báo khi cửa tủ bị hở hoặc mở quá lâu\r\n'),
	(8, 'Panasonic Inverter 255 lít NR-BV280QSVN', 1, 1, 20, 11590000, 'Ngăn cấp đông mềm thế hệ mới Prime Fresh+ bảo quản thực phẩm không cần rã đông\r\nTủ lạnh Inverter tiết kiệm điện tối đa, vận hành êm ái, ổn định\r\nTrang bị cảm biến Econavi, tủ lạnh nâng cao hiệu quả tiết kiệm điện hơn\r\nKiểu dáng sang trọng, hiện đại với ngăn đá dưới tiện lợi\r\nCông nghệ kháng khuẩn Ag Clean với tinh thể bạc Ag+ ngăn chặn vi khuẩn, mùi hôi\r\nCông nghệ làm lạnh Panorama mang hơi lạnh tỏa đều mọi vị trí trong tủ\r\nDung tích sử dụng 255 lít cho gia đình 2 - 3 người\r\nNhìn chung, tủ lạnh Panasonic Inverter 255 lít NR-BV280QSVN bên cạnh thiết kế nổi bật, mới lạ thì nó còn được trang bị nhiều công nghệ tiên tiến, hỗ trợ tốt cho bạn trong quá trình sử dụng như: Inverter tiết kiệm điện, ngăn cấp đông mềm tiện lợi, kháng khuẩn, khử mùi tinh thể bạc,... Với các tính năng hấp dẫn, cùng dung tích 255 lít, sản phẩm này sẽ là một sự lựa chọn lý tưởng cho gia đình có 2 - 3 thành viên.'),
	(9, 'Samsung Inverter 655 lít RS62R5001M9/SV', 1, 0, 50, 22900000, 'Tủ lạnh Side by Side sang trọng, đẳng cấp\r\nDung tích 655 lít dành cho gia đình đông thành viên\r\nCông Nghệ Digital Inverter mang đến khả năng tiết kiệm điện hiệu quả\r\nMang hơi lạnh lan tỏa đồng đều đến mọi nơi bên trong tủ với công nghệ làm lạnh vòm\r\nBộ lọc khử mùi than hoạt tính loại bỏ mùi hôi khó chịu, duy trì độ thơm ngon của thực phẩm\r\nChuông báo cửa mở tiện lợi, tránh lãng phí điện năng\r\nVừa được ra mắt trong năm 2019, chiếc tủ lạnh Samsung RS62R5001M9/SV không chỉ mang kiểu dáng Side by Side hiện đại, dung tích lớn mà nó còn nổi bật nhờ công nghệ SpaceMax với thiết kế viền siêu mỏng, giúp khai phóng không gian, cho phép bạn lưu trữ và bảo quản được nhiều thực phẩm hơn những chiếc tủ lạnh đời cũ. Nếu gia đình bạn có thói quen mua nhiều thực phẩm để dùng một lần thì chiếc tủ lạnh Samsung Side by Side này chính là một sự lựa chọn đáng cân nhắc. \r\n'),
	(10, 'LG Inverter 266 lít GV-B262WB', 1, 1, 10, 10990000,'Tủ lạnh LG Inverter 266 lít GV-B262WB có khả năng tiết kiệm điện hiệu quả và vận hành êm ái. Hơn nữa, mẫu tủ lạnh này có thể duy trì được độ tươi ngon thực phẩm và làm lạnh đồ uống tối ưu khi đặt ở phía cánh cửa tủ lạnh nhờ các công nghệ đặc biệt. '),
	(11, 'LG Inverter 217 lít GV-B212WB', 1, 0, 20, 8990000, 'Kiểu tủ ngăn đá trên quen thuộc với cửa bằng thép không gỉ chắc chắn.
